import React, { Component } from "react";
import { Link, animateScroll as scroll } from "react-scroll";
import '../css/nav.css'
import logo from '../img/bean.png'
import styled from 'styled-components';

const Navbar = styled.nav`
  back
  width: 100%;
  border-bottom: 2px solid #f1f1f1;
  padding: 0 20px;
  display: flex;
  justify-content: space-between;
  .logo {
    padding: 15px 0;
  }
`


export default class Navigation extends Component {
  scrollToTop = () => {
    scroll.scrollToTop();
  };

  render() {
    return (
      <Navbar>
      <nav className="nav"  id="navbar">
      <div className='logo'>
        <img
          src={logo}

          alt="Logo"
          onClick={this.scrollToTop}
        />
        <ul className="nav-items">
          <li className="nav-item">
            <Link
              activeClass="active"
              to="about"
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
            >
              About
            </Link>
          </li>
          <li className="nav-item">
            <Link
              activeClass="active"
              to="process"
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
            >
              Process
            </Link>
          </li>
          <li className="nav-item">
            <Link
              activeClass="active"
              to="contact"
              spy={true}
              smooth={true}
              offset={-70}
              duration={500}
            >
              Contact Us
            </Link>
          </li>
        </ul>
      </div>
    </nav>
    </Navbar>
    );
  }
}
